package binary_search

class LongestDuplicateSubstringKotlin1044 {

    private var str: String = ""

    data class Trie(
        var links: Array<Trie?>?,
        val position: Int,
        val depth: Int
    )

    fun longestDupSubstring(S: String): String {
        this.str = S
        var maxStart = 0
        var max = 0
        val root = Trie(null, 0, 0)
        var current = 1
        while (current + max <= S.length) {
            val result = addNew(root, current);
            if (result > max) {
                max = result
                maxStart = current
            }
            current++
        }
        return S.substring(maxStart, maxStart + max)
    }

    private fun addNew(root: Trie, length: Int): Int {
        val depth = root.depth
        if (length + depth == str.length) {
            return depth
        }
        if (root.links == null) {
            root.links = Array(26) { null }
            root.links!![str[root.position + root.depth] - 'a'] =
                Trie(null, root.position, depth + 1)
        }
        val nextNodeIndex = str[length + root.depth] - 'a'
        return if (root.links!![nextNodeIndex] == null) {
            root.links!![nextNodeIndex] = Trie(null, length, depth + 1)
            depth
        } else {
            addNew(root.links!![nextNodeIndex]!!, length)
        }
    }
